package com.example.asmtest;import androidx.appcompat.app.AppCompatActivity;import android.content.pm.ApplicationInfo;import android.content.pm.PackageManager;import android.os.Bundle;import android.util.Log;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        isAppPrivacySyncSupported("xxxx");    }    public boolean isAppPrivacySyncSupported(String packageName) {        try {            ApplicationInfo ai = getPackageManager().getApplicationInfo(packageName, PackageManager.GET_META_DATA);            Bundle bundle = ai.metaData;            if (bundle != null) {                boolean privacySupported = bundle.getBoolean("sync_data_to_system_service", false);                return privacySupported;            }        } catch (Exception e) {        }        return false;    }    @Override    protected void onRestart() {        super.onRestart();    }    @Override    protected void onResume() {        super.onResume();    }    @Override    protected void onDestroy() {        super.onDestroy();    }}