package com.asm.plugin;import com.android.build.gradle.AppExtension;import org.gradle.api.Plugin;import org.gradle.api.Project;import java.util.Collections;public class AsmPlugin implements Plugin<Project> {    @Override    public void apply(Project project) {        System.out.println("================ com.asm.plugin.AsmPlugin =============");        AppExtension appExtension = (AppExtension)project.getProperties().get("android");        appExtension.registerTransform(new AsmTransform(), Collections.EMPTY_LIST);    }}