package com.miadapm.plugin.aspect;import org.aspectj.lang.JoinPoint;import org.aspectj.lang.annotation.Aspect;import org.aspectj.lang.annotation.Before;import org.aspectj.lang.annotation.Pointcut;import shadow.bundletool.com.android.ddmlib.Log;@Aspectpublic class AspectAdMethodTime {    @Pointcut("call(* com.example.asmtest.Animal.fly(..))")    public void callMethod() {    }    @Before("callMethod()")    public void beforeMethodCall(JoinPoint joinPoint) {        Log.e("sundu", "before->" + joinPoint.getTarget().toString());    }}