package com.xiaomi.miadaspect;import android.util.Log;import org.aspectj.lang.JoinPoint;import org.aspectj.lang.ProceedingJoinPoint;import org.aspectj.lang.annotation.Around;import org.aspectj.lang.annotation.Aspect;import org.aspectj.lang.annotation.Pointcut;@Aspectpublic class AspectAdMethodTime {    @Pointcut("execution(* com.example.asmtest.MainActivity.onCreate(..))")    public void callMethod() {    }    @Around("callMethod()")    public void beforeMethodCall(ProceedingJoinPoint joinPoint) {        Log.e("AspectJ", "before" );        long startNanoTime = System.nanoTime();        try {            Object proceed = joinPoint.proceed();        } catch (Throwable throwable) {            throwable.printStackTrace();        }        Log.e("AspectJ", "after" );        long stopNanoTime = System.nanoTime();        Log.e("AspectJ","method time = "+(stopNanoTime - startNanoTime));    }}