package com.xiaomi.miadaspect;import android.util.Log;import org.aspectj.lang.JoinPoint;import org.aspectj.lang.annotation.Aspect;import org.aspectj.lang.annotation.Before;import org.aspectj.lang.annotation.Pointcut;@Aspectpublic class AspectAdMethodTime {    @Pointcut("call(* com.example.asmtest.Animal.fly(..))")    public void callMethod() {    }    @Before("callMethod()")    public void beforeMethodCall(JoinPoint joinPoint) {        Log.e("sundu", "before->" + joinPoint.getTarget().toString());    }}